$(document).ready(function() {
	"use strict";
	$("#navbar-affix").affix({
		offset: {
			top: 100
		}
	}), $("a").on("click", function(t) {
		if ("" !== this.hash) {
			t.preventDefault();
			var o = this.hash;
			$("html, body").animate({
				scrollTop: $(o).offset().top
			}, 1e3, function() {
				window.location.hash = o
			})
		}
	})
});
